import React, { useState } from "react";
import {
  Box,
  Image,
  VStack,
  HStack,
  Heading,
  Text,
  Center,
  OrderedList,
  ListItem,
} from "@chakra-ui/react";

import MenuBar from "./MenuBar";

import HomeBG from "./assets/home-background.svg";

const loremThree =
  "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Amet quam cras lacus nullam amet. Egestas varius accumsan sit nulla turpis turpis sem elit.";

function Home() {
  return (
    <>
      <MenuBar />
      <Image src={HomeBG} position="absolute" zIndex="-2" />
      <Center pt="130px">
        <HomeContent />
      </Center>
    </>
  );
}

function HomeContent() {
  return (
    <VStack w="70vw" spacing={36}>
      {/* --- Motto / Graphic --- */}
      <HStack w="100%" justifyContent="space-between">
        <Heading align="center" fontSize="5xl" fontWeight="light">
          Give us your time,{<br />}see your truth
        </Heading>
        <Box bg="gray.300" w="500px" h="400px"></Box>
      </HStack>

      <VStack spacing={24}>
        {/* --- Header / Text / Img --- */}
        <HStack w="100%" justifyContent="space-between">
          <Box h="70%" w="35%">
            <Heading fontWeight="light">Header</Heading>
            <Text mt="0.5em">{loremThree}</Text>
          </Box>
          <Box bg="gray.300" w="450px" h="280px"></Box>
        </HStack>

        {/* --- Img / Header / Text --- */}
        <HStack w="100%" justifyContent="space-between">
          <Box bg="gray.300" w="450px" h="280px"></Box>
          <Box h="70%" w="35%">
            <Heading fontWeight="light">Header</Heading>
            <Text mt="0.5em">{loremThree}</Text>
          </Box>
        </HStack>
      </VStack>

      {/* --- Select from list thing --- */}
      <FeatureSelector />
    </VStack>
  );
}

function FeatureSelector() {
  const [currFeat, setCurrFeat] = useState();
  const features = [
    { id: 1, text: "Feature 1", active: false, imgSrc: "" },
    { id: 2, text: "Feature 2", active: false, imgSrc: "" },
    { id: 3, text: "Feature 3", active: false, imgSrc: "" },
    { id: 4, text: "Feature 4", active: false, imgSrc: "" },
    { id: 5, text: "Feature 5", active: false, imgSrc: "" },
  ];

  function FeatureImg({ text, imgSrc }) {
    return (
      <>
        <Box bg="gray.300" w="550px" h="400px"></Box>
      </>
    );
  }

  return (
    <HStack px={8}>
      <Box>
        <Heading fontWeight="light">Header</Heading>
        <OrderedList
          mt="0.5em"
          pl={4}
          fontSize="xl"
          fontWeight="light"
          lineHeight="180%"
        >
          <ListItem>Feature 1</ListItem>
          <ListItem>Feature 2</ListItem>
          <ListItem>Feature 3</ListItem>
          <ListItem>Feature 4</ListItem>
          <ListItem>Feature 5</ListItem>
        </OrderedList>
      </Box>
    </HStack>
  );
}

export default Home;
